mapper.insert:
	INSERT INTO test_picture (
		name
		, user_id
		, created_at
		, updated_at
	) VALUES (
		#{name}
		, #{userId}
		, NOW()
		, NOW()
	)

        SELECT LAST_INSERT_ID()

	INSERT INTO test_picture_child (
		name
		, picture_id
		, url
		, created_at
		, updated_at
	) VALUES (
		#{name}
		, #{pictureId}
		, #{url}
		, NOW()
		, NOW()
	)

        SELECT LAST_INSERT_ID()

mapper.select:
SELECT tu.id AS userId , tp.name , tp.id , (SELECT COUNT(*) FROM test_picture_child WHERE test_picture_child.picture_id = tp.id) AS pictureCounts , tp.created_at , tp.updated_at FROM test_user tu , test_picture tp WHERE 1=1 AND tu.id = tp.user_id
AND tu.name = #{name}
ORDER BY tp.created_at ASC

SELECT tpc.id , tpc.url , tpc.name , tpc.created_at , tpc.updated_at , tpc.picture_id FROM test_picture tp , test_picture_child tpc WHERE 1=1 AND tp.id = tpc.picture_id
AND tp.id = #{id}
ORDER BY tpc.created_at DESC

mapper.insert:
	INSERT INTO test_tag (
		tag_name
		, picture_child_id
		, created_at
		, updated_at
	) VALUES (
		#{tagName}
		, #{pictureChildId}
		, NOW()
		, NOW()
	)

        SELECT LAST_INSERT_ID()

mapper.update:
UPDATE test_user
SET points = #{points}
	, updated_at = NOW()
WHERE id = #{id}
